tinymce.PluginManager.add("tooltip", function (t, o) { t.addMenuItem("tooltip", { text: "tooltip plugin", context: "tools", icon: !0, image: "https://d30y9cdsu7xlg0.cloudfront.net/png/384409-200.png", onclick: function () { t.windowManager.open({ title: "tooltip description", body: [{ type: "textbox", name: "title", label: "Description" }], onsubmit: function (o) { t.insertContent("<div>" + o.data.title + "</div></div></div>") } }), t.windowManager.open({ title: "tooltip image", body: [{ type: "textbox", name: "image", label: "Image URL" }], onsubmit: function (o) { t.insertContent('<div class="tooltip"><img src="' + o.data.image + '" width="100%" height="150" />') } }), t.windowManager.open({ title: "tooltip text", body: [{ type: "textbox", name: "word", label: "Word" }], onsubmit: function (o) { t.insertContent('<div class="wrapper">' + o.data.word) } }) } }) });